class Solution {public:    int subarraySum(vector<int>& nums, int k) {        if(nums.empty()==true)            return {};                int cursum=0;        int res=0;                unordered_map<int,int> mymap{{0,1}};                for(int i=0;i<nums.size();i++)        {            cursum+=nums[i];            int curtarget=cursum-k;            res=res+mymap[curtarget];            mymap[cursum]++;        }        return res;    }};//从第0个到第i个的和是多少》//在这i+1个元素中sum-k的情况出现了多少次？